[{"taobao.com":{"JS_Template":"window.fanliAdRemoverCount = 0;
window.fanliAdNumberRemoved = 0;
window.fanliAdElementIds = ['#J_popupwrap','#smartAd','.tmm-ad-mask','.c-smartpop','.c-smartpop-wrap','c:.*dsk'];

window.fanliRemoveSmartAd = function()
{
    window.fanliAdRemoverCount++;
	for (var idx = 0; idx < window.fanliAdElementIds.length; idx++)
	{
		if (window.fanliAdElementIds[idx].indexOf("c:") === 0) {
			var eles = document.querySelectorAll("div");
			for (var i = 0; i < eles.length; i++) {
				var ele = eles[i];
				if (new RegExp(window.fanliAdElementIds[idx].substring(2), "ig").test(ele.className) && window.getComputedStyle(ele).position == 'fixed') {
					ele.style.display = "none";
					ele.style.visibility = 'hidden';
					document.body.style.paddingTop = "0";
					window.fanliAdNumberRemoved ++;
					break;
				}
			}
		} else {
			var ele = document.querySelector (window.fanliAdElementIds[idx]);
			if (ele != null && 'none' != ele.style.display)
			{
				ele.style.display = 'none';
				ele.style.visibility = 'hidden';
				window.fanliAdNumberRemoved ++;
			}
		}	
	}

	if (window.fanliAdNumberRemoved < window.fanliAdElementIds.length)
	{
        window.setTimeout(function() {window.fanliRemoveSmartAd();}, 300);
	}
    else
    {
        window.fanliAdRemoverCount = null;
		window.fanliAdNumberRemoved = null;
		window.fanliAdElementIds = null;
        window.fanliRemoveSmartAd = null;
    }
};

