        if (window.androidCatch) {
            window.androidCatch.sendOrderInfo(data);
        }
        return data;
    };
    window.fltb.sendBackOrder = function (data) {
        try {
            if (data.abort) return window.fltb.sendBack(0, "ORDER_CATCH_ABORT");
            var status = data.ret[0].substring(0, data.ret[0].indexOf("::"));
            if (status != "SUCCESS") return window.fltb.sendBack(0, status);
            var orderData = data.data.orderInfo;
            var buyerData = data.data.buyerInfo;
            var sellerData = data.data.sellerInfo;
            var productData = orderData.orderCell;
            var promotion = orderData.promotion || [];
            if (orderData.tmallPoint) promotion.push("天猫积分:" + orderData.tmallPoint);
            var statusCode = window.fltb.orderStatusMap[orderData.orderStatus] || 0;
